package crawler;import java.sql.*;public class DB {    public Connection conn = null;    /**     * 构造对象时加载驱动     */    public DB() {        try {            Class.forName("com.mysql.jdbc.Driver");            String url = "jdbc:mysql://localhost:3306/Crawler";            conn = DriverManager.getConnection(url, "root", "123456");            System.out.println("conn built");        } catch (ClassNotFoundException e) {            e.printStackTrace();        } catch (SQLException e) {            e.printStackTrace();        }    }    /**     * 数据库查询语句     *     * @param sql     * @return     * @throws SQLException     */    public ResultSet runSql(String sql) throws SQLException {        Statement sta = conn.createStatement();        return sta.executeQuery(sql);    }    /**     * 数据库操作语句     *     * @param sql     * @return     * @throws SQLException     */    public boolean runSql2(String sql) throws SQLException {        Statement sta = conn.createStatement();        return sta.execute(sql);    }    /**     * 关闭数据库连接     */    protected void finalClose() {        try {            if (conn != null || !conn.isClosed()) conn.close();        } catch (SQLException e) {            e.printStackTrace();        }    }}